import React, { useState } from 'react';

const AppointmentForm = () => {
  const [formData, setFormData] = useState({
    patient_name: '',
    doctor_id: '',
    appointment_date: '',
    time_slot: ''
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setSuccess(false);

    try {
      if (!formData.patient_name.trim()) {
        setError('Please enter a valid patient name');
        return;
      }

      if (!formData.doctor_id || parseInt(formData.doctor_id) <= 0) {
        setError('Please enter a valid doctor ID');
        return;
      }

      const appointmentDateTime = new Date(formData.appointment_date + 'T' + formData.time_slot);
      if (appointmentDateTime < new Date()) {
        setError('Please select a future date and time');
        return;
      }

      const response = await fetch(`http://localhost:8000/doctors/${formData.doctor_id}/appointments/`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          patient_name: formData.patient_name.trim(),
          appointment_date: appointmentDateTime.toISOString(),
          time_slot: formData.time_slot
        }),
      });
      
      const data = await response.json();
      
      if (response.ok) {
        setSuccess(true);
        setFormData({
          patient_name: '',
          doctor_id: '',
          appointment_date: '',
          time_slot: ''
        });
      } else {
        setError(data.detail || 'Failed to book appointment. Please try again.');
      }
    } catch (error) {
      console.error('Error booking appointment:', error);
      setError('Failed to book appointment. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h2>Book Appointment</h2>
      {success && <div className="success-message">Appointment booked successfully!</div>}
      {error && <div className="error-message">{error}</div>}
      <form onSubmit={handleSubmit}>
        <div>
          <label>Patient Name:</label>
          <input
            type="text"
            value={formData.patient_name}
            onChange={(e) => setFormData({...formData, patient_name: e.target.value})}
            disabled={loading}
            required
          />
        </div>
        <div>
          <label>Doctor ID:</label>
          <input
            type="number"
            value={formData.doctor_id}
            onChange={(e) => setFormData({...formData, doctor_id: e.target.value})}
            disabled={loading}
            min="1"
            required
          />
        </div>
        <div>
          <label>Date:</label>
          <input
            type="date"
            value={formData.appointment_date}
            onChange={(e) => setFormData({...formData, appointment_date: e.target.value})}
            disabled={loading}
            min={new Date().toISOString().split('T')[0]}
            required
          />
        </div>
        <div>
          <label>Time:</label>
          <input
            type="time"
            value={formData.time_slot}
            onChange={(e) => setFormData({...formData, time_slot: e.target.value})}
            min="09:00"
            max="17:00"
            required
          />
        </div>
        <button type="submit" disabled={loading}>
          {loading ? 'Booking...' : 'Book Appointment'}
        </button>
      </form>
    </div>
  );
};

export default AppointmentForm;
